Rule {
   Reaction SCAN ACTOR_PROCESS ScanAction REPORT
   Process {
      Include OBJECT_NAME { -v "explorer.exe" -v "svchost.exe" }
      Exclude OBJECT_NAME { -v "C:\\Windows\\System32\\explorer.exe" -v "C:\\Windows\\System32\\svchost.exe" }
   }
   Target {
      Match NETWORK {
         Include -protocol "TCP"
         Include -port "445"
         Include -direction "OUTBOUND"
      }
   }
}
