Rule {
   Reaction SCAN ACTOR_PROCESS ScanAction REPORT
   Process {
      Include OBJECT_NAME { -v "**.exe" }
      Exclude OBJECT_NAME { -v "C:\\Program Files\\**" }
      Exclude OBJECT_NAME { -v "C:\\Program Files (x86)\\**" }
      Exclude OBJECT_NAME { -v "C:\\Windows\\System32\\**" }
   }
   Target {
      Match NETWORK {
         Include -protocol "TCP"
         Include -port "80,443"
         Include -direction "OUTBOUND"
      }
   }
}
